INSERT INTO public.spots (place_id,"location",display_name,zona,created_at,updated_at,best_conditions,bad_conditions) VALUES
	 ('mdp_sunrider','SRID=4326;POINT (-57.53842974113251 -37.954876392505504)'::public.geometry,'Sunrider, Mar del Plata','Mar del Plata Norte','2025-10-11 19:00:34.889812-03','2025-10-11 19:00:34.889812-03','{"tide": ["Baja", "Media", "Alta"], "notes": "Point break de izquierdas en el norte. Tamaño ideal 2-6 pies. Nivel intermedio. Tener cuidado con rocas y corrientes.", "wave_type": ["Point break"], "wave_direction": ["Izquierda"], "wind_direction": ["O", "ONO"], "swell_direction": ["S", "SE"]}','{"notes": "Los vientos del este y sur son onshore o cross-onshore, generando condiciones desordenadas. No recibe oleaje del norte u oeste.", "wind_direction": ["E", "S", "SE"], "swell_direction": ["N", "O", "NE"]}'),
	 ('mdp_estrada','SRID=4326;POINT (-57.53972196992519 -37.9590353371911)'::public.geometry,'Estrada, Mar del Plata','Mar del Plata Norte','2025-10-11 19:00:34.902967-03','2025-10-11 19:00:34.902967-03','{"tide": ["Media", "Baja"], "notes": "Las mejores olas se forman cerca de las escolleras. Funciona bien con viento de tierra. Tamaño ideal 2-6 pies. Todos los niveles. Cuidado con espigones.", "wave_type": ["Beach break"], "wave_direction": ["Izquierda", "Derecha"], "wind_direction": ["O", "NO"], "swell_direction": ["S", "SE"]}','{"notes": "Los vientos onshore arruinan la formación de la ola. La marea alta puede hacer que la ola rompa contra la orilla (orillera).", "wind_direction": ["E", "S", "SE"], "swell_direction": ["N", "O", "NE"]}'),
	 ('mdp_puerto_cardiel','SRID=4326;POINT (-57.5434687530712 -37.9777066463614)'::public.geometry,'Puerto Cardiel, Mar del Plata','Mar del Plata Norte','2025-10-11 19:00:34.905424-03','2025-10-11 19:00:34.905424-03','{"tide": ["Media", "Baja"], "notes": "Olas de buena calidad, derechas e izquierdas. Muy popular entre los surfistas de la zona norte. Tamaño ideal 2-6 pies. Todos los niveles.", "wave_type": ["Beach break"], "wave_direction": ["Izquierda", "Derecha"], "wind_direction": ["O", "NO"], "swell_direction": ["S", "SE"]}','{"notes": "Los vientos del sur y sudeste son onshore y generan condiciones desordenadas.", "wind_direction": ["E", "S", "SE"], "swell_direction": ["N", "O", "NE"]}'),
	 ('mdp_la_perla','SRID=4326;POINT (-57.54452557294651 -37.99212382991072)'::public.geometry,'La Perla, Mar del Plata','Mar del Plata Norte','2025-10-11 19:00:34.908168-03','2025-10-11 19:00:34.908168-03','{"tide": ["Media", "Baja"], "notes": "Spot clásico con múltiples picos. Muy consistente con la combinación correcta de swell del sur y viento del oeste. Tamaño ideal 2-6 pies. Todos los niveles.", "wave_type": ["Beach break"], "wave_direction": ["Izquierda", "Derecha"], "wind_direction": ["O", "NO"], "swell_direction": ["S", "SE", "E"]}','{"notes": "Muy expuesto a los vientos del este, que son onshore y generan choppy. Puede haber mucha gente (multitudes).", "wind_direction": ["E", "S", "SE"], "swell_direction": ["N", "O"]}'),
	 ('mdp_la_pepita','SRID=4326;POINT (-57.540819831718196 -37.998756081219135)'::public.geometry,'La Pepita, Mar del Plata','Mar del Plata Norte','2025-10-11 19:00:34.913846-03','2025-10-11 19:00:34.913846-03','{"tide": ["Media", "Baja"], "notes": "Condiciones similares a La Perla. Los bancos de arena pueden ser cambiantes debido al refulado. Tamaño ideal 1-5 pies. Todos los niveles.", "wave_type": ["Beach break"], "wave_direction": ["Izquierda", "Derecha"], "wind_direction": ["O", "NO"], "swell_direction": ["S", "SE", "E"]}','{"notes": "Condiciones desfavorables idénticas a las de La Perla debido a su proximidad. Bancos cambiantes.", "wind_direction": ["E", "S", "SE"], "swell_direction": ["N", "O"]}'),
	 ('mdp_la_popular','SRID=4326;POINT (-57.540525250667514 -38.001072452613876)'::public.geometry,'La Popular, Mar del Plata','Mar del Plata Centro','2025-10-11 19:00:34.918614-03','2025-10-11 19:00:34.918614-03','{"tide": ["Media", "Baja"], "notes": "Spot céntrico y muy accesible. Puede estar muy concurrido. Tamaño ideal 2-5 pies. Todos los niveles.", "wave_type": ["Beach break"], "wave_direction": ["Izquierda", "Derecha"], "wind_direction": ["O", "NO"], "swell_direction": ["E", "SE", "S"]}','{"notes": "La calidad de la ola se degrada rápidamente con vientos onshore. La alta afluencia de gente (multitudes) puede ser un problema.", "wind_direction": ["E", "S", "SE"], "swell_direction": ["N", "O"]}'),
	 ('mdp_varese','SRID=4326;POINT (-57.530600418453446 -38.014150177536436)'::public.geometry,'Varese, Mar del Plata','Mar del Plata Centro','2025-10-11 19:00:34.920714-03','2025-10-11 19:00:34.920714-03','{"tide": ["Baja", "Media", "Alta"], "notes": "Ideal para principiantes. Necesita un swell grande para funcionar debido a la protección de las escolleras. Tamaño ideal 1-3 pies. Cuidado con espigones.", "wave_type": ["Beach break"], "wave_direction": ["Izquierda", "Derecha"], "wind_direction": ["O", "NO", "SO"], "swell_direction": ["E", "NE"]}','{"notes": "Estará plano con la mayoría de los swells (pequeño/mediano). Los vientos del este pueden generar un pequeño choppy incómodo.", "wind_direction": ["E", "NE"], "swell_direction": ["S", "SO", "O"]}'),
	 ('mdp_biologia','SRID=4326;POINT (-57.5301418875182 -38.02671185873845)'::public.geometry,'Biología, Mar del Plata','Mar del Plata Centro','2025-10-11 19:00:34.923278-03','2025-10-11 19:00:34.923278-03','{"tide": ["Media", "Baja"], "notes": "Conocido por su larga izquierda que rompe a lo largo del espigón. Para surfistas de nivel intermedio a avanzado. Tamaño ideal 3-7 pies. Fondo arena/roca.", "wave_type": ["Point break"], "wave_direction": ["Izquierda"], "wind_direction": ["O", "NO"], "swell_direction": ["S", "SE", "E"]}','{"notes": "Las corrientes pueden ser fuertes. El viento onshore destruye la línea de la ola. Cuidado con el espigón.", "wind_direction": ["E", "S", "SE"], "swell_direction": ["N", "O", "NE"]}'),
	 ('mdp_el_yacht','SRID=4326;POINT (-57.53152823940271 -38.03053787500099)'::public.geometry,'El Yacht, Mar del Plata','Mar del Plata Centro','2025-10-11 19:00:34.927323-03','2025-10-11 19:00:34.927323-03','{"tide": ["Baja", "Media", "Alta"], "notes": "Spot único que funciona con swell del NE. Cuidado con las fuertes corrientes de resaca (rips). Tamaño ideal 2-6 pies. Nivel intermedio. Fondo arena/roca.", "wave_type": ["Point break"], "wave_direction": ["Izquierda"], "wind_direction": ["ONO", "O"], "swell_direction": ["NE", "E"]}','{"notes": "Totalmente bloqueado de los swells del sur por la Escollera Sur. Los vientos del este son directamente onshore. Corrientes fuertes.", "wind_direction": ["E", "S", "SE"], "swell_direction": ["S", "SO", "O"]}'),
	 ('mdp_playa_grande','SRID=4326;POINT (-57.531672485373996 -38.0289283553591)'::public.geometry,'Playa Grande, Mar del Plata','Mar del Plata Centro','2025-10-11 19:00:34.931192-03','2025-10-11 19:00:34.931192-03','{"tide": ["Baja", "Media", "Alta"], "notes": "Playa muy grande y consistente con picos para todos los niveles. Un clásico de Mar del Plata. Tamaño ideal 2-7 pies. Fondo de arena.", "wave_type": ["Beach break"], "wave_direction": ["Izquierda", "Derecha"], "wind_direction": ["O", "NO"], "swell_direction": ["E", "SE", "S"]}','{"notes": "Puede estar muy concurrido, especialmente en verano (multitudes). Los vientos del este generan condiciones desordenadas.", "wind_direction": ["E", "S", "SE"], "swell_direction": ["N", "O"]}');
INSERT INTO public.spots (place_id,"location",display_name,zona,created_at,updated_at,best_conditions,bad_conditions) VALUES
	 ('mdp_waikiki','SRID=4326;POINT (-57.539109774025064 -38.080013395552186)'::public.geometry,'Waikiki, Mar del Plata','Mar del Plata Sur','2025-10-11 19:00:34.934215-03','2025-10-11 19:00:34.934215-03','{"tide": ["Baja", "Media", "Alta"], "notes": "Excelente para principiantes y longboarders. Olas suaves y largas. Muy consistente. Tamaño ideal 2-5 pies. Fondo arena/roca. Cuidado con rocas.", "wave_type": ["Point break"], "wave_direction": ["Derecha"], "wind_direction": ["SO", "O"], "swell_direction": ["S", "SE", "E", "NE"]}','{"notes": "Aunque maneja muchas condiciones, los vientos fuertes del este o sur lo desordenan. Puede haber mucha gente (multitudes).", "wind_direction": ["E", "S", "SE", "NE"], "swell_direction": ["N", "O"]}'),
	 ('mdp_mariano','SRID=4326;POINT (-57.53801429527699 -38.08285758034673)'::public.geometry,'Mariano, Mar del Plata','Mar del Plata Sur','2025-10-11 19:00:34.936728-03','2025-10-11 19:00:34.936728-03','{"tide": ["Media", "Baja"], "notes": "Izquierda de alta calidad, veloz y tubular sobre fondo de rocas. Para surfistas experimentados (intermedio/avanzado). Tamaño ideal 3-8 pies.", "wave_type": ["Reef break"], "wave_direction": ["Izquierda"], "wind_direction": ["NO", "O"], "swell_direction": ["SE", "S"]}','{"notes": "Peligroso con marea baja y swell grande. Los vientos onshore lo hacen impracticable. Rocas sumergidas son un peligro constante.", "wind_direction": ["E", "S", "SE"], "swell_direction": ["N", "O", "NE", "E"]}'),
	 ('mdp_la_serena','SRID=4326;POINT (-57.575693967525424 -38.108593034632605)'::public.geometry,'La Serena, Mar del Plata','Mar del Plata Sur','2025-10-11 19:00:34.938821-03','2025-10-11 19:00:34.938821-03','{"tide": ["Baja", "Media", "Alta"], "notes": "Playa extensa con múltiples picos, buena para iniciarse y surfistas de nivel intermedio/principiante. Tamaño ideal 2-6 pies. Fondo de arena.", "wave_type": ["Beach break"], "wave_direction": ["Izquierda", "Derecha"], "wind_direction": ["O", "NO", "SO"], "swell_direction": ["S", "SE", "E"]}','{"notes": "Como todo beach break expuesto, sufre mucho con los vientos onshore. Puede haber multitudes.", "wind_direction": ["E", "S", "SE", "NE"], "swell_direction": ["N", "O"]}'),
	 ('mdp_chapadmalal','SRID=4326;POINT (-57.69062083136213 -38.20687539673014)'::public.geometry,'Chapadmalal, Mar del Plata','Mar del Plata Sur','2025-10-11 19:00:34.940808-03','2025-10-11 19:00:34.940808-03','{"tide": ["Media", "Baja"], "notes": "Zona con olas potentes y de calidad para surfistas experimentados (intermedio/avanzado). Máxima exposición a los swells del Atlántico Sur. Tamaño ideal 4-10 pies. Fondo arena/roca.", "wave_type": ["Beach break"], "wave_direction": ["Izquierda", "Derecha"], "wind_direction": ["O", "NO", "SO"], "swell_direction": ["S", "SE"]}','{"notes": "Las fuertes corrientes y los fondos de roca/arena pueden ser peligrosos. No es un lugar para principiantes. Rocas y corrientes son peligros constantes.", "wind_direction": ["E", "S", "SE"], "swell_direction": ["N", "O", "NE", "E"]}');
